import { ClassType } from './isFunctions';
export declare function asString(value: unknown): asserts value is string;
export declare function asNumber(value: unknown): asserts value is number;
export declare function asBigInt(value: unknown): asserts value is bigint;
export declare function asBoolean(value: unknown): asserts value is boolean;
export declare function asSymbol(value: unknown): asserts value is symbol;
export declare function asUndefined(value: unknown): asserts value is undefined;
export declare function asNull(value: unknown): asserts value is null;
export declare function asNil(value: unknown): asserts value is null | undefined;
export declare function asDefined<T>(value: T): asserts value is NonNullable<T>;
export declare function asFunction<T>(value: unknown): asserts value is T;
export declare function asInstance<T>(value: unknown, C: ClassType<T>): asserts value is T;
/**
 *  This method will not check element types.
 */
export declare function asArray<ItemType>(value: unknown): asserts value is Array<ItemType>;
export declare function asObject(value: unknown): asserts value is {
    [key: string]: unknown;
};
